import { ADD_STUDENT, EDIT_STUDENT } from "./type";

export const actAddStudent = (student) => {
    return {
        type: ADD_STUDENT,
        payload: student,
    }
}

export const actEditStudent = (student) => {
    return {
        type: EDIT_STUDENT,
        payload: student,
    }
}
