import data from '../data.json'
import {ADD_STUDENT, EDIT_STUDENT, REMOVE_STUDENT, SEARCH} from './type'

const initialState = {
    studentList: data,
    keyword: "",
    studentEdit: null,
}



const studentReducer = (state = initialState, action) => {
    switch (action.type) {
        case ADD_STUDENT: {
            const cloneStudentList = [...state.studentList];
            if(action.payload.id){
                const index = cloneStudentList.findIndex((student) => student.id === action.payload.id)
                console.log(index);
                if(index != -1) {
                    cloneStudentList[index] = action.payload;
                }
            else{
                const student = {...action.payload}
                console.log(student);
                cloneStudentList.push(student)
                console.log(cloneStudentList);
            }}

            state.studentList = cloneStudentList;
            return { ...state };
        };
        case EDIT_STUDENT: {
            state.studentEdit = action.payload;
            console.log(state.studentEdit);
            return {...state}
        }

        case REMOVE_STUDENT: {
            const cloneStudentList = [state.studentList];
            const index = cloneStudentList.findIndex((id) => cloneStudentList.id === action.payload)
            if(index != -1){
                cloneStudentList.splice(index,1)
            }

            state.studentList = cloneStudentList;
            return {...state}
        };

        case SEARCH: {
            state.keyword = action.payload;
            return {...state}
        }

        default:
            return { ...state };
    }
}

export default studentReducer;
